// Load In Animation
gsap.from(".navbar__list-item", {
  opacity: 0,
  x: 50,
  y: -30,
  duration: 0.75,
  stagger: 0.25,
  delay: 0.5,
});

gsap.from(".navbar__brand", {
  opacity: 0,
  x: -50,
  y: -30,
  duration: 0.75,
});

gsap.from(".header__content > *", {
  opacity: 0,
  y: -50,
  duration: 0.75,
  stagger: 0.25,
  delay: 1,
});

// Scroll Spy
const navbarLinks = document.querySelectorAll(".navbar__link");

navbarLinks.forEach((link) => {
  link.addEventListener("click", function () {
    setTimeout(() => {
      navbarLinks.forEach((l) => l.classList.remove("active"));
      link.classList.add("active");
    }, 300);
  });
});

window.addEventListener("scroll", function () {
  const scrollSpySections = document.querySelectorAll(".scrollspy");

  scrollSpySections.forEach((v, i) => {
    let rect = v.getBoundingClientRect().y;
    if (rect < window.innerHeight - window.innerHeight / 2) {
      navbarLinks.forEach((v) => v.classList.remove("active"));
      navbarLinks[i].classList.add("active");
    }
  });

  // Navbar Animation On Scroll
  const navbar = document.querySelector(".navbar");
  if (window.scrollY > 10) {
    navbar.classList.add("navbar__scrolled");
  } else {
    navbar.classList.remove("navbar__scrolled");
  }
});

// Mobile Navigation
const menuIcon = document.querySelector(".navbar__menu-icon");
const mobileNav = document.querySelector(".mobile-nav");
const mobileNavCloseIcon = document.querySelector(".mobile-nav__close-icon");

menuIcon.addEventListener("click", function () {
  document.querySelector("body").style.overflow = "hidden";
  mobileNav.style.display = "flex";
  gsap.to(".mobile-nav", { opacity: 1, duration: 0.5 });
});

mobileNavCloseIcon.addEventListener("click", function () {
  document.querySelector("body").style.overflow = "auto";
  gsap.to(".mobile-nav", { opacity: 0, duration: 0.5 });
  setTimeout(() => {
    mobileNav.style.display = "none";
  }, 750);
});

document.querySelectorAll(".mobile-nav__link").forEach((link) => {
  link.addEventListener("click", function () {
    mobileNavCloseIcon.click();
  });
});

// Tab Functionality
const tabHeadings = document.querySelectorAll(".about-section__tab-heading");

tabHeadings.forEach((heading) => {
  heading.addEventListener("click", function () {
    if (!heading.classList.contains("active")) {
      document
        .querySelector(".about-section__tab-heading.active")
        .classList.remove("active");
      heading.classList.add("active");

      document
        .querySelector(".about-section__tab-content.active")
        .classList.remove("active");
      document
        .querySelector(`#tab-${heading.getAttribute("data-tab")}`)
        .classList.add("active");
    }
  });
});

