const projectCards = document.querySelectorAll(".portfolio-section__card");
const modal = document.querySelector(".project-modal");
const overlay = document.querySelector("#project-modal-overlay");

const updateProjectModalContent = (index) => {
  const p = projectData[index];
  const base = "project-modal";

  // Update Title
  document.querySelector(`.${base}__title`).textContent = p.title;

  // Update Type
  document.querySelector(`.${base}__type`).textContent = p.type;

  // Update Description
  document.querySelector(`.${base}__desc`).textContent = p.desc;

  // Update Tech Used
  document.querySelector(`.${base}__tech-used`).innerHTML = "";

  p.tech.forEach((t) => {
    document.querySelector(`.${base}__tech-used`).innerHTML += `
            <div class="${base}__tech-item">
                ${t.icon}
                <span class="${base}__tech-item-name">${t.text}</span>
            </div>
        `;
  });

  // Update Links
  document.querySelector("#live-project").setAttribute("href", p.liveProject);
  document.querySelector("#source-code").setAttribute("href", p.srcCode);

  // Update Background Image
  document.querySelector(
    `.${base}__img-section`
  ).style.backgroundImage = ` url("../assets/images/work-screenshots/${p.backgroundImg}")`;
};

projectCards.forEach((card) => {
  card.addEventListener("click", function () {
    updateProjectModalContent(parseInt(card.getAttribute("data-project")));

    overlay.style.display = "block";

    gsap.to("#project-modal-overlay", { opacity: 1, duration: 0.5 });

    modal.style.display = "block";
    gsap.to(".project-modal", { opacity: 1, duration: 0.5 });
  });
});

const closeModal = () => {
  gsap.to("#project-modal-overlay", { opacity: 0, duration: 0.5 });
  gsap.to(".project-modal", { opacity: 0, duration: 0.5 });

  setTimeout(() => {
    overlay.style.display = "none";
    modal.style.display = "none";
  }, 500);
};

document
  .querySelector(".project-modal__close-icon")
  .addEventListener("click", closeModal);
overlay.addEventListener("click", closeModal);

