// Fetch Blog Posts
const fetchBlogPosts = async () => {
  const query = `
    query GetUserArticles($page: Int!) {
        user(username: "BlakeYeboah") {
            publication {
                posts(page: $page) {
                    title
                    brief
                    slug
                }
            }
        }
    }
`;
  const variables = { page: 0 };
  const data = await fetch("https://api.hashnode.com/", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query,
      variables,
    }),
  });

  const result = await data.json();

  const articles = result.data.user.publication.posts.slice(0, 3); // Get First Three

  articles.forEach((article) => {
    let desc = article.brief.replace(/(\r\n|\n|\r)/gm, " ").substr(0, 146);

    document.querySelector(".blog-section__posts").innerHTML += `
        <div class="blog-section__post">
            <h4 class="blog-section__post-title">${article.title}</h4>
            <p class="blog-section__post-brief">${desc}...</p>
            <a href="https://blog.blakeyeboah.com/${article.slug}" target="_blank" class="blog-section__post-read-post">READ POST ${readPostIcon}</a>
        </div>
    `;
  });
};

document.addEventListener("DOMContentLoaded", function () {
  // Fetch Blog Posts
  fetchBlogPosts();

  // Set Year Dynamically
  const year = new Date().getFullYear();
  document.querySelector("#year").textContent = year;

  // Clear Input Fields
  document.querySelectorAll(".contact-us__form-input").forEach((input) => {
    input.value = "";
  });
  document.querySelector(".contact-us__form-textarea").value == "";
});

// Handle Form Submission
const form = document.querySelector(".contact-us__form");
const errorAlert = document.querySelector(".contact-us__form-alert");
const alertClose = document.querySelector(".contact-us__form-alert-close");

form.addEventListener("submit", (e) => {
  // Validate Inputs
  let hasError = false;

  document.querySelectorAll(".contact-us__form-input").forEach((input) => {
    if (input.value == "" && input.getAttribute("name") != "phone") {
      hasError = true;
    }
  });

  if (document.querySelector(".contact-us__form-textarea").value == "") {
    hasError = true;
  }

  if (hasError) {
    e.preventDefault();
    errorAlert.style.display = "flex";
    errorAlert.scrollIntoView({ block: "center" });
  } else {
    errorAlert.style.display = "none";
  }
});

alertClose.addEventListener("click", () => {
  errorAlert.style.display = "none";
});
